#!/bin/bash
set -x
# Assuming that the results of test_comparison, generated by script
# check_ref_comparison.sh are considered valid,
# move them to the referenece_results directory

# Fist arg must be the directory hosting test comparison results
outdir=${1:-/thredds/ipsl/ssenesi/C-ESM-EP/test_comparison_ssenesi}

# Assuming that this script is in a cesmep_tests-like directory
cd $(dirname $0)

# Copy the results and rename some files
rm -fR reference_results/*
mv $outdir/* reference_results
for html in $(ls reference_results/*/atlas_*.html); do
    mv $html ${html/test_comparison/reference_comparison}
done
for fil in $(ls reference_results/*/*png_diff.png); do
    rm $fil
done
for dir in $(ls -d reference_results/*/diffs); do
    rm -fR $dir
done

# Comitting all that is not done by this script
